&#34; printf &#34;${C_RESET}&#34; } # 获取系统信息 get_systatus_info() { sys_os=$(uname -o) sys_release=$(cat /etc/redhat-release) sys_kernel=$(uname -r) sys_hostname=$(hostname) sys_selinux=$(getenforce) sys_lang=$(echo $LANG) sys_lastreboot=$(who -b | awk &#39;{print $3,$4}&#39;) sys_runtime=$(uptime | awk &#39;{print $3,$4}&#39; | cut -d, -f1) sys_time=$(date) sys_load=$(uptime | cut -d: -f5) cat &lt;&lt; EOF 【系统信息】 系统: ${sys_os} 发行版本: ${sys_release} 系统内核: ${sys_kernel} 主机名: ${sys_hostname} selinux状态: ${sys_selinux} 系统语言: ${sys_lang} 系统当前时间: ${sys_time} 系统最后重启时间: ${sys_lastreboot} 系统运行时间: ${sys_runtime} 系统负载: ${sys_load} EOF } # 获取CPU信息 get_cpu_info() { Physical_CPUs=$(grep &#34;physical id&#34; /proc/cpuinfo | sort | uniq | wc -l) Virt_CPUs=$(grep &#34;processor&#34; /proc/cpuinfo | wc -l) CPU_Kernels=$(grep &#34;cores&#34; /proc/cpuinfo | uniq | awk -F &#39;: &#39; &#39;{print $2}&#39;) CPU_Type=$(grep &#34;model name&#34; /proc/cpuinfo | awk -F &#39;: &#39; &#39;{print $2}&#39; | sort | uniq) CPU_Arch=$(uname -m) cat &lt;&lt; EOF 【CPU信息】 物理CPU个数:$Physical_CPUs 逻辑CPU个数:$Virt_CPUs 每CPU核心数:$CPU_Kernels CPU型号:$CPU_Type CPU架构:$CPU_Arch EOF } # 获取服务信息 get_service_info() { port_listen=$(netstat -lntup | grep -v &#34;Active Internet&#34;) kernel_config=$(sysctl -p 2&gt; /dev/null) if [[ ${sysversion} -gt 6 ]]; then service_config=$(systemctl list-unit-files --type=service --state=enabled | grep &#34;enabled&#34;) run_service=$(systemctl list-units --type=service --state=running | grep &#34;.service&#34;) else service_config=$(/sbin/chkconfig | grep -E &#34;:on|:启用&#34; | column -t) run_service=$(/sbin/service --status-all | grep -E &#34;running&#34;) fi cat &lt;&lt; EOF 【服务信息】 ${service_config} ${line} 运行的服务: ${run_service} ${line} 监听端口: ${port_listen} ${line} 内核参考配置: ${kernel_config} EOF } # 获取系统内存信息 get_mem_info() { check_mem=$(free -m) MemTotal=$(grep MemTotal /proc/meminfo | awk &#39;{print $2}&#39;) #KB MemFree=$(grep MemFree /proc/meminfo | awk &#39;{print $2}&#39;) #KB let MemUsed=MemTotal-MemFree MemPercent=$(awk &#34;BEGIN {if($MemTotal==0){printf 100}else{printf \&#34;%.2f\&#34;,$MemUsed*100/$MemTotal}}&#34;) report_MemTotal=&#34;$((MemTotal/1024))&#34; &#34;MB&#34; #内存总容量(MB) report_MemFree=&#34;$((MemFree/1024))&#34; &#34;MB&#34; #内存剩余(MB) report_MemUsedPercent=$(free | sed -n &#39;2p&#39; | gawk &#39;x = int(( $3 / $2 ) * 100) {print x}&#39; | sed &#39;s/$/%/&#39;) cat &lt;&lt; EOF 【内存信息】 内存总容量(MB): ${report_MemTotal} 内存剩余量(MB):${report_MemFree} 内存使用率: ${report_MemUsedPercent} EOF } # 获取系统网络信息 get_net_info() { pri_ipadd=$(ip addr | awk &#39;/^[0-9]+: / {}; /inet.*global/ {print gensub(/(.*)/(.*)/, &#34;\1&#34;, &#34;g&#34;, $2)}&#39;) pub_ipadd=$(curl ifconfig.me -s) gateway=$(ip route | grep default | awk &#39;{print $3}&#39;) mac_info=$(ip link | egrep -v &#34;lo&#34; | grep link | awk &#39;{print $2}&#39;) dns_config=$(egrep -v &#34;^$|^#&#34; /etc/resolv.conf) route_info=$(route -n) cat &lt;&lt; EOF 【网络信息】 系统公网地址:${pub_ipadd} 系统私网地址:${pri_ipadd} 网关地址:${gateway} MAC地址:${mac_info} 路由信息: ${route_info} DNS 信息: ${dns_config} EOF } # 获取系统磁盘信息 get_disk_info() { disk_info=$(fdisk -l | grep &#34;Disk /dev&#34; | cut -d, -f1) disk_use=$(df -hTP | awk &#39;$2!=&#34;tmpfs&#34;{print}&#39;) disk_percent=$(free | sed -n &#39;2p&#39; | gawk &#39;x = int(( $3 / $2 ) * 100) {print x}&#39; | sed &#39;s/$/%/&#39;) disk_inode=$(df -hiP | awk &#39;$1!=&#34;tmpfs&#34;{print}&#39;) cat &lt;&lt; EOF 【磁盘信息】 ${disk_info} 磁盘使用: ${disk_use} 磁盘使用百分比: ${disk_percent} inode信息: ${disk_inode} EOF } # 获取系统用户信息 get_sys_user() { login_user=$(awk -F: &#39;{if ($NF==&#34;/bin/bash&#34;) print $0}&#39; /etc/passwd) ssh_config=$(egrep -v &#34;^#|^$&#34; /etc/ssh/sshd_config) sudo_config=$(egrep -v &#34;^#|^$&#34; /etc/sudoers | grep -v &#34;^Defaults&#34;) host_config=$(egrep -v &#34;^#|^$&#34; /etc/hosts) crond_config=$(for cronuser in /var/spool/cron/*; do ls ${cronuser} 2&gt; /dev/null | cut -d/ -f5; egrep -v &#34;^$|^#&#34; ${cronuser} 2&gt; /dev/null; echo &#34;&#34;; done) cat &lt;&lt; EOF 【用户信息】 系统登录用户: ${login_user} ${line} ssh 配置信息: ${ssh_config} ${line} sudo 配置用户: ${sudo_config} ${line} 定时任务配置: ${crond_config} ${line} hosts 信息: ${host_config} EOF } # 获取CPU/内存占用TOP10 get_process_top_info() { top_title=$(top -b n1 | head -7 | tail -1) cpu_top10=$(top -b n1 | head -17 | tail -11) mem_top10=$(top -b n1 | head -17 | tail -10 | sort -k10 -r) cat &lt;&lt; EOF 【TOP10】 CPU占用TOP10: ${cpu_top10} 内存占用TOP10: ${top_title} ${mem_top10} EOF } show_dead_process() { printf &#34;僵尸进程：
&#34; ps -al | gawk &#39;{print $2,$4}&#39; | grep Z } get_all_info() { get_systatus_info echo ${double_line} get_service_info echo ${double_line} get_cpu_info echo ${double_line} get_net_info echo ${double_line} get_mem_info echo ${double_line} get_disk_info echo ${double_line} get_process_top_info echo ${double_line} get_sys_user } main() { while [[ 1 ]] do printMenu read option local index=$[ ${option} - 1 ] case ${options[${index}]} in &#34;获取系统信息&#34;) get_systatus_info ;; &#34;获取服务信息&#34;) get_service_info ;; &#34;获取CPU信息&#34;) get_cpu_info ;; &#34;获取系统网络信息&#34;) get_net_info ;; &#34;获取系统内存信息&#34;) get_mem_info ;; &#34;获取系统磁盘信息&#34;) get_disk_info ;; &#34;获取CPU/内存占用TOP10&#34;) get_process_top_info ;; &#34;获取系统用户信息&#34;) get_sys_user ;; &#34;输出所有信息&#34;) get_all_info &gt; sys.log printf &#34;${C_GREEN}信息已经输出到 sys.log 中。${C_RESET}

