#include&lt;stdio.h&gt;
#include&lt;math.h&gt;
int main()
{
float a,b,c;//一元二次方程三系数
if(scanf(&quot;%f %f %f&quot;,&amp;a,&amp;b,&amp;c)!=3)
{
printf(&ldquo;input error!&rdquo;);
return 0;
}
if(a==0&amp;&amp;b==0)//a,b都为0和abc都为零
{
if(c==0)
printf(&ldquo;Zero Equation&rdquo;);
else
printf(&ldquo;Not An Equation&rdquo;);
return 0;
}
if(a==0)//当a=0且b!=0,一元一次方程
{
printf(&quot;%0.2f&quot;,-c/b);
return 0;
}
float Delta=b*b-4*a*c;//定义Delta
if(Delta&lt;0)//三种情况之1:两虚根
{
if(b==0)//b为0时无实部
printf(&ldquo;0.00+%0.2fi\n0.00-%0.2fi&rdquo;,sqrt(-Delta)/(2*a),sqrt(-Delta)/(2*a));
else
printf(&quot;%0.2f+%0.2fi\n%0.2f-%0.2fi&quot;,(-b) / (2 * a),sqrt(-Delta) / (2 * a),(-b) / (2 * a),sqrt(-Delta) / (2 * a));
}
else if(Delta==0)//三种情况之2:两相同实根
printf(&quot;%0.2f&quot;,-b/(2*a));
else//三种情况之3:两不同实根
printf(&quot;%0.2f\n%0.2f&quot;,(-b+sqrt(b*b-4*a*c))/(2*a),(-b-sqrt(b*b-4*a*c))/(2*a));
return 0 ;
}

